mapper.select:
SELECT user.user_id, user.user_name, user.user_nickname, user.user_cn_name, user.user_mobile, user.user_certificate_type, user.user_certificate_number, user.user_id_card_back_img, user.user_status, user.user_create_time, user.user_register_from, company.company_id, company.company_name, company.company_contact, company.company_principal_id_code, company.company_unified_social_img, company.company_license_no_img, company.company_tax_no_img, company.company_organization_code_img, company.company_is_three_in_one, company.company_unified_social_credit_ldentifier, company.company_license_no FROM company LEFT JOIN user ON company.company_Id=user.user_company_id
and user.user_create_time >= #{startTime}
and user.user_create_time <= DATE_ADD(#{endTime},INTERVAL 1 DAY)
and user.user_certificate_type=#{type}
and user.user_status=#{status}
and ( POSITION(#{keyword} IN user_name) OR POSITION(#{keyword} IN user_mobile) OR POSITION(#{keyword} IN company_name) OR POSITION(#{keyword} IN company_contact) OR POSITION(#{keyword} IN user_certificate_number) OR POSITION(#{keyword} IN company_principal_id_code) )
ORDER BY user_create_time DESC
