mapper.select:
SELECT a.account_sub_id , a.account_sub_no, a.account_transaction_phone,
account_sub_relation_id, account_sub_no
from account_sub a WHERE a.account_sub_no = #{accountSub}

mapper.update:
update account_sub
set
account_transaction_phone = #{accountTransactionPhone}
where account_sub_create_user_id = #{accountSubCreateUserId}
and account_sub_no = #{accountSubNo}
