mapper.select:
SELECT go.*, usp.user_name, comp.company_name issueCompanyName, comr.company_name receiveCompanyName FROM goods_order go LEFT JOIN USER usp ON usp.user_id = go.order_create_user_id LEFT JOIN USER usr ON usr.user_id = go.order_receive_user_id LEFT JOIN company comp ON comp.company_id = go.order_company_id LEFT JOIN company comr ON comr.company_id = go.order_receive_company_id
go.order_company_id = #{issueCompanyId}
and go.order_issue_time >= #{startTime}
and go.order_issue_time <= DATE_ADD(#{endTime},INTERVAL 1 DAY)
and go.order_assign=#{orderAssign}
and go.order_status=#{orderStatus}
and go.order_issue_time < CURRENT_TIMESTAMP
and go.order_issue_time > CURRENT_TIMESTAMP
and ( POSITION(#{keyword} IN go.order_goods_name) OR POSITION(#{keyword} IN go.order_goods_weight) OR POSITION(#{keyword} IN go.order_goods_cube) OR POSITION(#{keyword} IN go.order_money) OR POSITION(#{keyword} IN go.order_pickup_province) OR POSITION(#{keyword} IN go.order_pickup_city) OR POSITION(#{keyword} IN go.order_pickup_district) OR POSITION(#{keyword} IN go.order_receive_province) OR POSITION(#{keyword} IN go.order_receive_city) OR POSITION(#{keyword} IN go.order_receive_district) OR POSITION(#{keyword} IN go.order_pickup_address) OR POSITION(#{keyword} IN go.order_receive_address) OR POSITION(#{keyword} IN comr.company_name) )
ORDER BY go.order_issue_time DESC

SELECT go.*, usp.user_name, comp.company_name issueCompanyName, comr.company_name receiveCompanyName FROM goods_order go LEFT JOIN USER usp ON usp.user_id = go.order_create_user_id LEFT JOIN USER usr ON usr.user_id = go.order_receive_user_id LEFT JOIN company comp ON comp.company_id = go.order_company_id LEFT JOIN company comr ON comr.company_id = go.order_receive_company_id
(go.order_receive_company_id is null or go.order_receive_company_id = '' or (go.order_receive_company_id = #{recevieCompanyId} ) ) and go.order_issue_time < CURRENT_TIMESTAMP
and go.order_issue_time >= #{startTime}
and go.order_issue_time <= DATE_ADD(#{endTime},INTERVAL 1 DAY)
and go.order_assign=#{orderAssign}
and go.order_status=#{orderStatus}
and go.order_issue_time < CURRENT_TIMESTAMP
and go.order_issue_time > CURRENT_TIMESTAMP
and ( POSITION(#{keyword} IN go.order_goods_name) OR POSITION(#{keyword} IN go.order_goods_weight) OR POSITION(#{keyword} IN go.order_goods_cube) OR POSITION(#{keyword} IN go.order_money) OR POSITION(#{keyword} IN go.order_pickup_province) OR POSITION(#{keyword} IN go.order_pickup_city) OR POSITION(#{keyword} IN go.order_pickup_district) OR POSITION(#{keyword} IN go.order_receive_province) OR POSITION(#{keyword} IN go.order_receive_city) OR POSITION(#{keyword} IN go.order_receive_district) OR POSITION(#{keyword} IN go.order_pickup_address) OR POSITION(#{keyword} IN go.order_receive_address) OR POSITION(#{keyword} IN comp.company_name) )
ORDER BY go.order_issue_time DESC
